import React from 'react';

const Berita3 = () => {

  const news = [
    { id: 1, title: 'Berita 1', description: 'Ini adalah deskripsi berita pertama.', image: 'https://via.placeholder.com/150' },
    { id: 2, title: 'Berita 2', description: 'Ini adalah deskripsi berita kedua.', image: 'https://via.placeholder.com/150' },
    { id: 3, title: 'Berita 3', description: 'Ini adalah deskripsi berita ketiga.', image: 'https://via.placeholder.com/150' }
  ];

  return (
    <div className="container mx-auto ">
      <h2 className="text-xl font-semibold mb-4 text-center">Progam Spesial KitaBisa</h2>
      <div className="grid grid-cols-1 sm:grid-cols-2 md:grid-cols-3 gap-6 max-w-lg mx-auto">
        {news.map((item) => (
          <div key={item.id} className="flex flex-col items-center p-4 border rounded-lg shadow-lg">
            <div className="bg-gray-100 rounded-full p-4 mb-2">
              <img src={item.image} alt={item.title} className="w-24 h-24 rounded-full" />
            </div>
            <h3 className="text-lg font-semibold text-center">{item.title}</h3>
            <p className="text-sm text-gray-600 text-center">{item.description}</p>
          </div>
        ))}
      </div>
    </div>
  );
}

export default Berita3;
