import React from "react";

const Rekomendasi = () => {

    const newsList = [
        {
            title: '5 Tahun Tanpa Masjid, 164 Warga Ibadah di Lapangan',
            description: 'Jagabersama',
            imageUrl: 'https://kitabisa.com/_next/image?url=https%3A%2F%2Fimgix.kitabisa.com%2Fb3303ca8-0ca2-4f61-b730-97c4f8ef2041.jpg%3Fauto%3Dformat%26fm%3Dpjpg%26ch%3DWidth%2CDPR%2CSave-Data%2CViewport-Width&w=256&q=75', // Ganti dengan URL gambar yang sesuai
        },
        {
          title: 'URGENT! Asap Tebal Kebakaran Hutan Kepung Kalbar',
          description: 'BAZNAZ Hub',
          imageUrl: 'https://kitabisa.com/_next/image?url=https%3A%2F%2Fimgix.kitabisa.com%2F76fb9c38-0999-414c-a382-eb0f40ba6db0.jpg%3Fauto%3Dformat%26fm%3Dpjpg%26ch%3DWidth%2CDPR%2CSave-Data%2CViewport-Width&w=256&q=75', // Ganti dengan URL gambar 2
        },
        {
          title: 'Solidaritas Bantu Korban Banjir Bandang Ternate',
          description: 'Masjid Nusantara',
          imageUrl: 'https://kitabisa.com/_next/image?url=https%3A%2F%2Fimgix.kitabisa.com%2F1e92a184-ee77-4bad-9aa3-d9ed0dc1b031.jpg%3Fauto%3Dformat%26fm%3Dpjpg%26ch%3DWidth%2CDPR%2CSave-Data%2CViewport-Width&w=640&q=75', // Ganti dengan URL gambar 3
        },
      ];
    
      return (
        <div className="bg-sky-200 p-6 rounded-lg max-w-lg mx-auto">
      {/* Label Rekomendasi */}
      <div className="bg-red-500 text-white text-xs py-1 px-2 rounded w-max mb-4">
        Rekomendasi
      </div>

      {/* Render setiap berita */}
      {newsList.map((news, index) => (
        <div key={index} className="flex items-center mb-6">
          {/* Gambar */}
          <img src={news.imageUrl} alt="News" className="w-40 h-30 object-cover mr-2" />
          {/* Teks berita */}
          <div className="flex flex-col">
            <h2 className="text-sm font-bold">{news.title}</h2>
            <p className="text-sm text-gray-700 mt-2">{news.description}</p>
          </div>
        </div>
      ))}
    </div>
      );
    };

    
        

    
export default Rekomendasi ;
