import React from 'react';

const Berita = () => {
  const news = [
    { id: 1, title: 'Berita 1', description: 'Ini adalah deskripsi berita pertama.', image: 'https://kitabisa.com/_next/image?url=https%3A%2F%2Fimgix.kitabisa.com%2F3a4b364d-34f6-4c54-9ac9-b517e48c1852.jpg%3Fauto%3Dformat%26fm%3Dpjpg%26ch%3DWidth%2CDPR%2CSave-Data%2CViewport-Width&w=640&q=75' },
    { id: 2, title: 'Berita 2', description: 'Ini adalah deskripsi berita kedua.', image: 'https://kitabisa.com/_next/image?url=https%3A%2F%2Fimgix.kitabisa.com%2Ff35cc842-93c9-49d0-b78f-315d2320f33c.jpg%3Fauto%3Dformat%26fm%3Dpjpg%26ch%3DWidth%2CDPR%2CSave-Data%2CViewport-Width&w=256&q=75' },
    { id: 3, title: 'Berita 3', description: 'Ini adalah deskripsi berita ketiga.', image: 'https://kitabisa.com/_next/image?url=https%3A%2F%2Fimgix.kitabisa.com%2Fee440aa7-8405-41c0-9a36-e281a2bc0d09.jpg%3Fauto%3Dformat%26fm%3Dpjpg%26ch%3DWidth%2CDPR%2CSave-Data%2CViewport-Width&w=256&q=75' }
  ];

  return (
    <div className="container mx-auto">
      <h2 className="text-xl font-semibold mb-4 text-center">Penggalangan Dana Mendesak</h2>
      <div className="grid grid-cols-1 sm:grid-cols-2 md:grid-cols-3 gap-6 max-w-lg mx-auto">
        {news.map((item) => (
          <div key={item.id} className="flex flex-col items-center p-4 border rounded-lg shadow-lg">
            <div className="bg-gray-100 p-4 mb-2">
              {/* Gambar ukuran 200x150 */}
              <img src={item.image} alt={item.title} className="w-[150px] h-[150px] object-cover" />
            </div>
            <h3 className="text-lg font-semibold text-center">{item.title}</h3>
            <p className="text-sm text-gray-600 text-center">{item.description}</p>
            <a href={item.image} target="_blank" rel="noopener noreferrer" className="mt-2 text-blue-500 hover:underline">
              Lihat Detail
            </a>
          </div>
        ))}
      </div>
    </div>
  );
}

export default Berita;
