import React from 'react';
import pasienkanker from '../Assets/pasienkanker.jpg';
const Berita = () => {

        const news = [
          { id: 1, title: 'Bantu Pasien Kanker Sembuh', image: {pasienkanker}, description: 'Ini adalah deskripsi berita pertama.' },
          { id: 2, title: 'Bantu Putu Bagus', img: 'https://kitabisa.com/campaign/kitabantuputubagus', description: 'Ini adalah deskripsi berita kedua.' },
          { id: 3, title: 'Makan Lauk Seadanya untuk Santri', img: 'https://kitabisa.com/campaign/makanlaukseadanyasantriicg', description: 'Ini adalah deskripsi berita ketiga.' }
        ];
      
        return (
            <div className="container mx-auto ">
              <h2 className="text-xl font-semibold mb-4 text-center">Penggalangan Dana Mendesak</h2>
              <div className="grid grid-cols-1 sm:grid-cols-2 md:grid-cols-3 gap-6 max-w-lg mx-auto">
                {news.map((item) => (
                  <div key={item.id} className="flex flex-col items-center p-4 border rounded-lg shadow-lg">
                    <div className="bg-gray-100 rounded-full p-4 mb-2">
                      <img src={item.image} alt={item.title} className="w-24 h-24 rounded-full" />
                    </div>
                    <h3 className="text-lg font-semibold text-center">{item.title}</h3>
                    <p className="text-sm text-gray-600 text-center">{item.description}</p>
                  </div>
                ))}
              </div>
            </div>
          );
        }


export default Berita;
