import React from "react";

const Rekomendasi = () => {

    const newsList = [
        {
            title: '5 Tahun Tanpa Masjid, 164 Warga Ibadah di Lapangan',
            description: 'Ini adalah ringkasan singkat dari berita terbaru yang direkomendasikan untuk Anda. Klik untuk membaca lebih lanjut.',
            imageUrl: 'https://kitabisa.com/_next/image?url=https%3A%2F%2Fimgix.kitabisa.com%2Fb3303ca8-0ca2-4f61-b730-97c4f8ef2041.jpg%3Fauto%3Dformat%26fm%3Dpjpg%26ch%3DWidth%2CDPR%2CSave-Data%2CViewport-Width&w=256&q=75', // Ganti dengan URL gambar yang sesuai
        },
        {
          title: 'Berita Kedua yang Harus Dibaca',
          description: 'Ini adalah ringkasan berita kedua. Sangat menarik dan relevan untuk hari ini.',
          imageUrl: 'https://via.placeholder.com/150', // Ganti dengan URL gambar 2
        },
        {
          title: 'Berita Terakhir yang Tidak Boleh Dilewatkan',
          description: 'Ringkasan berita ketiga yang sangat penting untuk dibaca, jangan lewatkan informasinya!',
          imageUrl: 'https://via.placeholder.com/150', // Ganti dengan URL gambar 3
        },
      ];
    
      return (
        <div className="space-y-6">
          {newsList.map((news, index) => (
            <div key={index} className="bg-sky-200 p-6 rounded-lg max-w-lg mx-auto relative">
              {/* Label Rekomendasi di kiri atas */}
              <div className="absolute top-2 left-2 bg-red-500 text-white text-xs py-1 px-2 rounded">
                Rekomendasi
              </div>
              {/* Konten berita: gambar dan teks */}
              <div className="flex items-center mt-6">
                <img src={news.imageUrl} alt="News" className="w-32 h-32 object-cover mr-4" />
                <div className="flex flex-col">
                  <h2 className="text-lg font-bold">{news.title}</h2>
                  <p className="text-sm text-gray-700 mt-2">{news.description}</p>
                </div>
              </div>
            </div>
          ))}
        </div>
      );
    };

    
        

    
export default Rekomendasi ;
