import React from 'react';

const Berita = () => {

        const news = [
          { id: 1, title: 'Berita 1', description: 'Ini adalah deskripsi berita pertama.' },
          { id: 2, title: 'Berita 2', description: 'Ini adalah deskripsi berita kedua.' },
          { id: 3, title: 'Berita 3', description: 'Ini adalah deskripsi berita ketiga.' }
        ];
      
        return (
            <div className="container mx-auto ">
            <h2 className="text-xl font-semibold mb-4 text-center">Penggalangan Dana Mendesak</h2>
            <div className="grid h-full max-w-lg grid-cols-4 mx-auto">
              {deeds.map((deed) => (
                <div key={deed.name} className="flex flex-col items-center">
                  <div className="bg-white-100 rounded-full p-4 mb-2">
                  <img src={deed.news} alt={deed.name} className="w-12 h-12" />
                  </div>
                  <p className="text-sm font-medium text-center">
                    {deed.name}
                  </p>
                </div>
              ))}
            </div>
          </div>
        );
      }



export default Berita;
