import React from "react";

const Rekomendasi = () => {

   
            const news = {
              title: 'Berita Utama Hari Ini',
              description: 'Ini adalah ringkasan singkat dari berita terbaru yang direkomendasikan untuk Anda. Klik untuk membaca lebih lanjut.',
              imageUrl: 'https://via.placeholder.com/150', // Ganti dengan URL gambar yang sesuai
            };
          
            return (
              <div className="news-container">
                <div className="recommendation-label">Rekomendasi</div>
                <div className="news-content">
                  <img src={news.imageUrl} alt="News" className="news-image" />
                  <div className="news-text">
                    <h2>{news.title}</h2>
                    <p>{news.description}</p>
                  </div>
                </div>
              </div>
            );
          };
        

    
export default Rekomendasi ;
